import React from 'react';
import ReactDOM from 'react-dom';
import {Route, BrowserRouter as Router, Switch} from 'react-router-dom';

import './index.css';
import HomePage from './components/Homepage';
import CountryPage from './components/CountryPage';

import firebase from './firebase';
let ref = firebase.firestore().collection('countries');

let timeInterval = (t) => new Promise((res, rej) => setTimeout(_ => res(), t));

ref.onSnapshot(async (snapshot) => {
	for ( let doc of snapshot.docs ) {
		fetch('https://api.covid19api.com/country/'+doc.id).then(d => d.json).then(console.log);
	}

	await timeInterval(10000);
	console.log('round')
  });

ReactDOM.render(
  <Router>
		<Switch>
			<Route exact path="/:country" component={CountryPage}/>
			<Route path="/" component={HomePage}/>
		</Switch> 
  </Router>,
  document.getElementById('root')
);
