import React from 'react';

import './CovidSummary.css';

export default class CovidSummary extends React.Component {
    constructor() {
        super();
        this.state = {
            loading: true,
            data: null
        }
    }

    componentDidMount() {
        let execute = (resp) => {
            this.state.loading = true;
        };

        let retry = () => {
            fetch('https://api.covid19api.com/summary').then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        };
    }

    render() {
        return (
            <div>
                <table className="summary">
                    <thead>
                        <tr className="grey">
                            <td>Coronavirus Summary Worldwide</td>
                        </tr>
                    </thead>
                    <tbody>
                        <tr className="yellow">
                            <td>Total Cases</td>
                            <td>...</td>
                        </tr>
                        <tr className="yellow">
                            <td>New Cases</td>
                            <td>...</td>
                        </tr>
                        <tr className="yellow">
                            <td>Active Cases</td>
                            <td>...</td>
                        </tr>

                        <tr className="blue">
                            <td>Total Recovered</td>
                            <td>...</td>
                        </tr>
                        <tr className="blue">
                            <td>New Recovered</td>
                            <td>...</td>
                        </tr>
                        <tr className="blue">
                            <td>Recovery Rate</td>
                            <td>...</td>
                        </tr>

                        <tr className="red">
                            <td>Total Deaths</td>
                            <td>...</td>
                        </tr>
                        <tr className="red">
                            <td>New Deaths</td>
                            <td>...</td>
                        </tr>
                        <tr className="red">
                            <td>Mortality Rate</td>
                            <td>...</td>
                        </tr>
                    </tbody>
                </table>
            </div>
            
        )
    }
};
