import React from 'react';

import './CovidPie.css';

let {am4core, am4charts} = window;

export default class CovidPie extends React.Component {
    constructor() {
        super();
        this.state = {am4chart: false, data: null};
    }

    shouldComponentUpdate(nextProps, nextState) {
        return !(nextState.data == null);
    }

    componentDidMount() {
        am4core.ready(() => {
            let custom_theme = (target) => {
                if (target instanceof am4core.ColorSet) {
                    target.list = [
                        am4core.color("#c44569"),
                        am4core.color("#546de5"),
                        am4core.color("#f5cd79")
                    ];
                }
            };

            am4core.useTheme(custom_theme);
            this.setState({am4chart: am4core.create("covidpiechart", am4charts.PieChart)});
        });

        let execute = (resp) => {
            this.setState({
                data: [
                    {label: "Dead Cases", count: resp.Global.TotalDeaths},
                    {label: "Recovered Cases", count: resp.Global.TotalRecovered},
                    {label: "Active Cases", count: resp.Global.TotalConfirmed - resp.Global.TotalRecovered}
                ]
            });

            let pieSeries = this.state.am4chart.series.push(new am4charts.PieSeries());
            pieSeries.dataFields.value = "count";
            pieSeries.dataFields.category = "label";
            pieSeries.slices.template.stroke = am4core.color("#fff");
            pieSeries.slices.template.strokeWidth = 2;
            pieSeries.slices.template.strokeOpacity = 1;
        };

        let retry = () => {
            fetch('https://api.covid19api.com/summary').then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        }; retry();
    }

    render() {
        return (
            <table className="summary">
                <thead>
                    <tr className="grey">
                        <td>Coronavirus Cases Distribution Worldwide</td>
                    </tr>
                </thead>

                <tbody style={{display: !this.state.data ? "none" : "initial"}}>
                    <tr><td id="covidpiechart">
                    </td></tr>
                </tbody>

                {this.state.data ?
                <tbody>
                    <tr className="yellow">
                        <td>Total Cases</td>
                        <td>{ this.state.data.TotalConfirmed.toLocaleString('en') }</td>
                    </tr>
                    <tr className="yellow">
                        <td>New Cases</td>
                        <td>{ this.state.data.NewConfirmed.toLocaleString('en') }</td>
                    </tr>
                    <tr className="yellow">
                        <td>Active Cases</td>
                        <td>{ (this.state.data.TotalConfirmed - this.state.data.TotalRecovered).toLocaleString('en') }</td>
                    </tr>

                    <tr className="blue">
                        <td>Total Recovered</td>
                        <td>{ this.state.data.TotalRecovered.toLocaleString('en') }</td>
                    </tr>
                    <tr className="blue">
                        <td>New Recovered</td>
                        <td>{ this.state.data.NewRecovered.toLocaleString('en') }</td>
                    </tr>
                    <tr className="blue">
                        <td>Recovery Rate</td>
                        <td>{ (this.state.data.TotalRecovered/this.state.data.TotalConfirmed*100).toFixed(2).toLocaleString('en') + ' %' }</td>
                    </tr>

                    <tr className="red">
                        <td>Total Deaths</td>
                        <td>{ this.state.data.TotalDeaths.toLocaleString('en') }</td>
                    </tr>
                    <tr className="red">
                        <td>New Deaths</td>
                        <td>{ this.state.data.NewDeaths.toLocaleString('en') }</td>
                    </tr>
                    <tr className="red">
                        <td>Mortality Rate</td>
                        <td>{ (this.state.data.TotalDeaths/this.state.data.TotalConfirmed*100).toFixed(2).toLocaleString('en') + ' %' }</td>
                    </tr>
                </tbody>
                    :
                <tbody>
                    <tr><td>
                        <div className="loading">
                            <div></div>
                            <div></div>
                            <div></div>
                        </div>
                    </td></tr>
                </tbody>
                }
                
                
            </table>
        );
    }
}
