import React from 'react';
import {useParams} from 'react-router-dom';


import CovidSummary from './CovidSummary';
import CovidPie from './CovidPie';
import DailyCovid from './DailyCovid';
import CovidGraph from './CovidGraph';

export default class HomePage extends React.Component {
    constructor() {
        super();
    }

    countryName(name) {
        return name.split('-').map(x => x[0].toUpperCase() + x.slice(1)).join(' ');
    }

    render() {
        let {country} = useParams();
        return (
            <div id="homepage">
                <CovidSummary title={"Coronavirus Summary In" + this.countryName(country)} load={() => fetch('https://api.covid19api.com/summary')} extract={(resp) => resp[country]}/>

                <CovidPie title={"Coronavirus Cases Distribution In" + this.countryName(country)} name={this.props.name} load={() => fetch("https://api.covid19api.com/summary")} extract={resp => {
                    return [
                        {label: "Dead Cases", count: resp[this.props.name].TotalDeaths},
                        {label: "Recovered Cases", count: resp[this.props.name].TotalRecovered},
                        {label: "Active Cases", count: resp[this.props.name].TotalConfirmed - resp.Global.TotalRecovered}
                    ];
                }}/>

                <DailyCovid title={"Daily Coronavirus Cases In" + this.countryName(country)} name={country} load={() => fetch("https://api.covid19api.com/total/country/" + country)} extract={resp => {
                    console.log('X', resp)
                    resp = resp.slice(-8);
                    for ( let i = 7; i >= 1; i-- ) {
                        resp[i].Deaths -= resp[i-1].Deaths;
                        resp[i].Confirmed -= resp[i-1].Confirmed;
                        resp[i].Recovered -= resp[i-1].Recovered;
                    }
                    console.log('ended')
                    return resp.slice(-7);
                }}/>

                <CovidGraph title={"Total Coronavirus Cases In" + this.countryName(country)} name="france" load={() => fetch("https://api.covid19api.com/total/country/france")} extract={resp => {
                    return resp;
                }} />
            </div>
        )
    }
}
