import React from 'react';

import './CovidPie.css';

let {am4core, am4charts, am4themes_animated} = window;

export default class CovidPie extends React.Component {
    constructor() {
        super();
        this.state = {am4chart: false, data: null};
    }

    shouldComponentUpdate(nextProps, nextState) {
        return !(nextState.data == null);
    }

    componentDidMount() {
        am4core.ready(() => {
            this.setState({am4chart: am4core.create("covidpiechart", am4charts.PieChart)});
        });

        let execute = (resp) => {
            this.setState({
                data: [
                    {label: "Dead Cases", count: resp.Global.TotalDeaths, color: am4core.color("#c44569")},
                    {label: "Recovered Cases", count: resp.Global.TotalRecovered, color: am4core.color("#546de5")},
                    {label: "Active Cases", count: resp.Global.TotalConfirmed - resp.Global.TotalRecovered, color: am4core.color("#f5cd79")}
                ]
            });

            let chart = this.state.am4chart;
            let pieSeries = chart.series.push(new am4charts.PieSeries());
            pieSeries.dataFields.value = "count";
            pieSeries.dataFields.category = "label";
            pieSeries.slices.template.propertyFields.fill = "color";

            chart.radius = '100px';
            chart.innerRadius = am4core.percent(25);
            
            // pieSeries.slices.template.stroke = am4core.color("#574b90");
            // pieSeries.slices.template.strokeWidth = 2;
            // pieSeries.slices.template.strokeOpacity = 1;

            pieSeries.ticks.template.disabled = true;
            pieSeries.alignLabels = false;
            pieSeries.labels.template.text = "{value.percent.formatNumber('#.0')}%";
            pieSeries.labels.template.radius = am4core.percent(-40);
            pieSeries.labels.template.fill = am4core.color("#fff");

            pieSeries.labels.template.adapter.add("radius", function(radius, target) {
                if (target.dataItem && (target.dataItem.values.value.percent < 10)) {
                    return 0;
                }
                return radius;
            });

            chart.legend = new am4charts.Legend();
            chart.legend.fontSize = 12;
            let marker = chart.legend.markers.template.children.getIndex(0);
            marker.cornerRadius(12, 12, 12, 12);
            marker.fill = am4core.color('white');
            chart.legend.markers.template.width = chart.legend.markers.template.height = 10;
              
            pieSeries.labels.template.adapter.add("fill", function(color, target) {
                if (target.dataItem && (target.dataItem.values.value.percent < 10)) {
                    return am4core.color("#fff");
                }
                return color;
            });



            pieSeries.hiddenState.properties.opacity = 1;
            pieSeries.hiddenState.properties.endAngle = -90;
            pieSeries.hiddenState.properties.startAngle = -90;
            chart.data = this.state.data;
        };

        let retry = () => {
            fetch('https://api.covid19api.com/summary').then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        }; retry();
    }

    render() {
        return (
            <table className="summary">
                <thead>
                    <tr className="grey">
                        <td>Coronavirus Cases Distribution Worldwide</td>
                    </tr>
                </thead>

                <tbody style={{display: !this.state.data ? "none" : "table"}}>
                    <tr><td id="covidpiechart">
                    </td></tr>
                </tbody>

                {this.state.data == null ?
                    <tbody className="full">
                    <tr><td>
                            <div className="loading">
                                <div></div>
                                <div></div>
                                <div></div>
                            </div>
                        </td></tr>
                    </tbody>
                    :
                    null
                }
                
  
                
                
                
            </table>
        );
    }
}
