import React from 'react';
import ReactDOM from 'react-dom';
import {Route, BrowserRouter as Router, Switch} from 'react-router-dom';

import './index.css';
import HomePage from './components/Homepage';
import CountryPage from './components/CountryPage';

import firebase from './firebase';
let firestore = firebase.firestore();
let ref = firestore.collection('countries');

let timeInterval = (t) => new Promise((res, rej) => setTimeout(_ => res(), t));

ref.get().then(async (snapshot) => {
	let failures = [];
	for ( let doc of snapshot.docs ) {
		let h = await ref.doc(doc.id).get();
		if (h.data().history.length) { console.log('skipping'); continue; }

		let data = await fetch('https://api.covid19api.com/country/'+doc.id).then(d => d.json());
		if ( data.message || data.length == 0 ) {
			console.log('failure in', doc.id);
			failures.push(doc.id);
			continue;
			//handle failure
			//--start 2020-01-23T00:00:00Z
		} else {

			console.log(data);
			let d = data[0], history = [];
			console.log(d);
			let value = {
				Date: d.Date,
				TotalConfirmed: d.Confirmed,
				NewConfirmed: d.Confirmed,
				TotalRecovered: d.Recovered,
				NewRecovered: d.Recovered,
				TotalDeaths: d.Deaths,
				NewDeaths: d.Deaths
			};

			history.push(Object.assign({}, value));
			for ( let i = 1; i < data.length; i++ ) {
				d = data[i];
				let newValue = {
					Date: d.Date,
					TotalConfirmed: d.Confirmed,
					NewConfirmed: d.Confirmed - value.TotalConfirmed,
					TotalRecovered: d.Recovered,
					NewRecovered: d.Recovered - value.TotalRecovered,
					TotalDeaths: d.Deaths,
					NewDeaths: d.Deaths - value.TotalDeaths
				};

				value = Object.assign(value, newValue);
				history.push(newValue);
			}

			await ref.doc(doc.id).set({history});	
		}
		await timeInterval(10000);

	}

	console.log('DONE', failures);
  });

ReactDOM.render(
  <Router>
		<Switch>
			<Route exact path="/:country" component={CountryPage}/>
			<Route path="/" component={HomePage}/>
		</Switch> 
  </Router>,
  document.getElementById('root')
);
