import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
import CovidSummary from './components/CovidSummary';
import CovidPie from './components/CovidPie';
import DailyCovid from './components/DailyCovid';
import CovidGraph from './components/CovidGraph';
import CovidTable from './components/CovidTable';

let dailyData = [ //get from firebase
	{
		TotalDeaths: 15791,
		TotalConfirmed: 670392,
		TotalRecovered: 517882,
		day: Date.parse('06 Jan 2021 00:00:00 GMT')
	}, {
		TotalDeaths: 9534,
		TotalConfirmed: 586318,
		TotalRecovered: 470722,
		day: Date.parse('07 Jan 2021 00:00:00 GMT'),
	}, {
		TotalDeaths: 9265,
		TotalConfirmed: 639061,
		TotalRecovered: 343798,
		day:  Date.parse('08 Jan 2021 00:00:00 GMT')
	}, {
		TotalDeaths: 12820,
		TotalConfirmed: 761531,
		TotalRecovered: 469297,
		day: Date.parse('09 Jan 2021 00:00:00 GMT')
	}, {
		TotalDeaths: 15028,
		TotalConfirmed: 839312,
		TotalRecovered: 413558,
		day: Date.parse('10 Jan 2021 00:00:00 GMT')
	}, {
		TotalDeaths: 14884,
		TotalConfirmed: 833433,
		TotalRecovered: 469222,
		day: Date.parse('11 Jan 2021 00:00:00 GMT')
	}, {
		TotalDeaths: 14844,
		TotalConfirmed: 808271,
		TotalRecovered: 527852,
		day: Date.parse('12 Jan 2021 00:00:00 GMT')
	}
].reverse(); dailyData.json = () => dailyData;

ReactDOM.render(
  <React.StrictMode>
    <CovidSummary title="Coronavirus Summary Worldwide" load={() => fetch('https://api.covid19api.com/summary')} extract={(resp) => resp.Global}/>

    <CovidPie title="Coronavirus Cases Distribution Worldwide" name="worldwide" load={() => fetch("https://api.covid19api.com/summary")} extract={resp => {
		return [
			{label: "Dead Cases", count: resp.Global.TotalDeaths},
			{label: "Recovered Cases", count: resp.Global.TotalRecovered},
			{label: "Active Cases", count: resp.Global.TotalConfirmed - resp.Global.TotalRecovered}
		];
    }}/>

    <DailyCovid title="Daily Coronavirus Cases Worldwide" name="worldwide" load={() => new Promise((res, rej) => res(dailyData))} extract={x => x}/>
	<CovidGraph title="Total Coronavirus Cases in France" name="france" load={() => fetch("https://api.covid19api.com/total/country/france")} extract={resp => {
		return resp;
	}} />
	<CovidTable title="Coronavirus Cases By Country" load={() => fetch('https://api.covid19api.com/summary')} extract={resp => resp.Countries.sort(x => x.Country)}></CovidTable>
  </React.StrictMode>,
  document.getElementById('root')
);
