import React from 'react';

import './CovidTable.css';
import arrow_up from 'arrow_up.png';
//import arrow_up_active from 'arrow_up_active.png';

export default class CovidSummary extends React.Component {
    constructor() {
        super();
        this.state = {
            data: true
        }
    }

    componentDidMount() {
        let execute = (resp) => {
            let data = this.props.extract(resp);
            this.setState({
                data
            }, null); //will update from template
        };

        let retry = () => {
            this.props.load().then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        }; retry();
    }

    render() {
        return (
            <div>
                <h1 className="grey header">{this.props.title}</h1>
                {this.state.data ? 
                    <table className="summary">
                        <thead>
                            <tr>
                                <td>Country<br/><i className="arrow_up active"><img src={arrow_up}/></i><i className="arrow_down"><img src={arrow_up}/></i></td>
                            </tr>
                        </thead>
                        <tbody>
                            
                        </tbody>
                    </table>
                    :
                    <table className="summary">
                        <tbody>
                            <tr><td>
                                <div className="loading">
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div>
                            </td></tr>
                        </tbody>
                    </table>
                }
            </div>
        )
    }
};
