import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
import CovidSummary from './components/CovidSummary';
import CovidPie from './components/CovidPie';
import covid from './images/covid.png';
import DailyCovid from './components/DailyCovid';

let {am4core} = window;

ReactDOM.render(
  <React.StrictMode>
    <img src={covid} style={{width: '50px'}}/>
    <h1 style={{fontStyle: 'italic', fontSize: '1.5em'}}>Live Updates and Statistics</h1>
    <br/>
    <CovidSummary />
    <CovidPie title="Coronavirus Cases Distribution Worldwide" name="worldwide" url="https://api.covid19api.com/summary" extract={resp => {
		return [
			{label: "Dead Cases", count: resp.Global.TotalDeaths, color: am4core.color("#c44569")},
			{label: "Recovered Cases", count: resp.Global.TotalRecovered, color: am4core.color("#546de5")},
			{label: "Active Cases", count: resp.Global.TotalConfirmed - resp.Global.TotalRecovered, color: am4core.color("#f5cd79")}
		];
    }}/>
    <DailyCovid/>
  </React.StrictMode>,
  document.getElementById('root')
);
