import React from 'react';

import './DailyCovid.css';

let {am4core, am4charts} = window;

export default class DailyCovid extends React.Component {
    constructor() {
        super();
        this.state = {am4chart: false, data: null};
    }

    shouldComponentUpdate(nextProps, nextState) {
        return !(nextState.data == null);
    }

    componentDidMount() {
        am4core.ready(() => {
            //create chart
            am4core.useTheme(target => {
                target.list = [
                    am4core.color("#c44569"),
                    am4core.color("#546de5"),
                    am4core.color("#f5cd79")
                ];
            });
            let chart = am4core.create('covidxycluster', am4charts.XYChart);
            chart.legend = new am4charts.Legend()
            chart.legend.position = 'top'
            chart.legend.paddingBottom = 20
            chart.legend.labels.template.maxWidth = 95
            chart.legend.markers.template.width = chart.legend.markers.template.height = 10;
            chart.legend.fontSize = 12;
            let marker = chart.legend.markers.template.children.getIndex(0);
            chart.legend.labels.template.fill = am4core.color('#fff');
            chart.legend.valueLabels.template.fill = am4core.color('#fff');
            marker.cornerRadius(12, 12, 12, 12);

            let xAxis = chart.xAxes.push(new am4charts.CategoryAxis())
            xAxis.dataFields.category = 'day'
            xAxis.renderer.cellStartLocation = 0.1
            xAxis.renderer.cellEndLocation = 0.9
            xAxis.renderer.grid.template.location = 0;

            let yAxis = chart.yAxes.push(new am4charts.ValueAxis());
            yAxis.min = 0;

            let createSeries = (value, name) => {
                let series = chart.series.push(new am4charts.ColumnSeries())
                series.dataFields.valueY = value
                series.dataFields.categoryX = 'day'
                series.name = name
            
                series.events.on("hidden", arrangeColumns);
                series.events.on("shown", arrangeColumns);
            
                let bullet = series.bullets.push(new am4charts.LabelBullet())
                bullet.interactionsEnabled = false
                bullet.dy = 30;
                bullet.label.text = '{valueY}'
                bullet.label.fill = am4core.color('#ffffff')
            
                return series;
            }

            function arrangeColumns() {

                let series = chart.series.getIndex(0);
            
                let w = 1 - xAxis.renderer.cellStartLocation - (1 - xAxis.renderer.cellEndLocation);
                if (series.dataItems.length > 1) {
                    let x0 = xAxis.getX(series.dataItems.getIndex(0), "categoryX");
                    let x1 = xAxis.getX(series.dataItems.getIndex(1), "categoryX");
                    let delta = ((x1 - x0) / chart.series.length) * w;
                    if (am4core.isNumber(delta)) {
                        let middle = chart.series.length / 2;
            
                        let newIndex = 0;
                        chart.series.each(function(series) {
                            if (!series.isHidden && !series.isHiding) {
                                series.dummyData = newIndex;
                                newIndex++;
                            }
                            else {
                                series.dummyData = chart.series.indexOf(series);
                            }
                        })
                        let visibleCount = newIndex;
                        let newMiddle = visibleCount / 2;
            
                        chart.series.each(function(series) {
                            let trueIndex = chart.series.indexOf(series);
                            let newIndex = series.dummyData;
            
                            let dx = (newIndex - trueIndex + middle - newMiddle) * delta
            
                            series.animate({ property: "dx", to: dx }, series.interpolationDuration, series.interpolationEasing);
                            series.bulletsContainer.animate({ property: "dx", to: dx }, series.interpolationDuration, series.interpolationEasing);
                        })
                    }
                }
            }

            createSeries('TotalDeaths', 'Total Deaths');
            createSeries('TotalRecovered', 'Total Recovered');
            createSeries('TotalConfirmed', 'Total Confirmed');

            if ( this.state.data ) {
                chart.data = this.state.data;
            }

            this.setState({am4chart: chart});
        });

        let execute = (resp) => {
            let data = resp;

            if ( this.state.am4chart ) {
                let chart = this.state.am4chart;
                chart.data = data;
            }

            this.setState({
                data
            });
        };

        let retry = () => {
            //needs our database, api19 doesn't provide this
            // fetch('https://api.covid19api.com/summary').then(
            //     resp => resp.json()
            // ).then(
            //     execute
            // ).catch(err => retry);

            execute([
                {
                    TotalDeaths: 15791,
                    TotalConfirmed: 670392,
                    TotalRecovered: 517882,
                    day: '12 Jan',
                }, {
                    TotalDeaths: 9534,
                    TotalConfirmed: 586318,
                    TotalRecovered: 470722,
                    day: '11 Jan',
                }, {
                    TotalDeaths: 9265,
                    TotalConfirmed: 639061,
                    TotalRecovered: 343798,
                    day: '10 Jan'
                }, {
                    TotalDeaths: 12820,
                    TotalConfirmed: 761531,
                    TotalRecovered: 469297,
                    day: '9 Jan'
                }, {
                    TotalDeaths: 15028,
                    TotalConfirmed: 839312,
                    TotalRecovered: 413558,
                    day: '8 Jan'
                }, {
                    TotalDeaths: 14884,
                    TotalConfirmed: 833433,
                    TotalRecovered: 469222,
                    day: '7 Jan'
                }, {
                    TotalDeaths: 14844,
                    TotalConfirmed: 808271,
                    TotalRecovered: 527852,
                    day: '6 Jan'
                }
            ])
        }; retry();
    }

    render() {
        return (
            <table className="summary">
                <thead>
                    <tr className="grey">
                        <td>Daily Coronavirus Cases Worldwide</td>
                    </tr>
                </thead>

                <tbody style={{display: !this.state.data ? "none" : "table"}}>
                    <tr><td id="covidxycluster">
                    </td></tr>
                </tbody>

                {this.state.data == null ?
                    <tbody className="full">
                    <tr><td>
                            <div className="loading">
                                <div></div>
                                <div></div>
                                <div></div>
                            </div>
                        </td></tr>
                    </tbody>
                    :
                    null
                }
            </table>
        );
    }
}
