import React from 'react';

import './DailyCovid.css';

let {am4core, am4charts} = window;

export default class DailyCovid extends React.Component {
    constructor() {
        super();
        this.state = {am4chart: false, data: null};
    }

    shouldComponentUpdate(nextProps, nextState) {
        return !(nextState.data == null);
    }

    componentDidMount() {
        am4core.ready(() => {
            //create chart
            am4core.create('covidxycluster', am4charts.XYChart)
        });

        let execute = (resp) => {
            var xAxis = chart.xAxes.push(new am4charts.CategoryAxis())
            xAxis.dataFields.category = 'category'
            xAxis.renderer.cellStartLocation = 0.1
            xAxis.renderer.cellEndLocation = 0.9
            xAxis.renderer.grid.template.location = 0;
        };

        let retry = () => {
            fetch('https://api.covid19api.com/summary').then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        }; retry();
    }

    render() {
        return (
            <table className="summary">
                <thead>
                    <tr className="grey">
                        <td>Daily Coronavirus Cases Worldwide</td>
                    </tr>
                </thead>

                <tbody style={{display: !this.state.data ? "none" : "table"}}>
                    <tr><td id="covidxycluster">
                    </td></tr>
                </tbody>

                {this.state.data == null ?
                    <tbody className="full">
                    <tr><td>
                            <div className="loading">
                                <div></div>
                                <div></div>
                                <div></div>
                            </div>
                        </td></tr>
                    </tbody>
                    :
                    null
                }
            </table>
        );
    }
}
