import React from 'react';
import ReactDOM from 'react-dom';
import {Route, BrowserRouter as Router, Switch} from 'react-router-dom';

import './index.css';
import HomePage from './components/Homepage';
import CountryPage from './components/CountryPage';

import firebase from './firebase';
let firestore = firebase.firestore();
let ref = firestore.collection('countries');

let timeInterval = (t) => new Promise((res, rej) => setTimeout(_ => res(), t));
import admin from 'firebase/admin';
const FieldValue = firestore.FieldValue;

ref.onSnapshot(async (snapshot) => {
	for ( let doc of snapshot.docs ) {
		let data = await fetch('https://api.covid19api.com/country/'+doc.id).then(d => d.json());
		if ( data.message ) {
			//handle failure
			//--start 2020-01-23T00:00:00Z
		} else {
			let d = data[0];
			let value = {
				Date: data.Date,
				TotalConfirmed: d.Confirmed,
				NewConfirmed: d.Confirmed,
				TotalRecovered: d.Recovered,
				NewRecovered: d.Recovered,
				TotalDeaths: d.Deaths,
				NewDeaths: d.Deaths
			};

			ref.doc(doc.id).update('history', FieldValue.arrayUnion(value));
			for ( let i = 1; i < 2; i++ ) {
				let newValue = {
					Date: data.Date,
					TotalConfirmed: d.Confirmed,
					NewConfirmed: d.Confirmed - value.Confirmed,
					TotalRecovered: d.Recovered,
					NewRecovered: d.Recovered - value.Recovered,
					TotalDeaths: d.Deaths,
					NewDeaths: d.Deaths - value.Deaths
				};

				Object.assign(value, newValue);

				ref.doc(doc.id, FieldValue.arrayUnion(newValue));
			}
		}
		await timeInterval(10000);

	}
  });

ReactDOM.render(
  <Router>
		<Switch>
			<Route exact path="/:country" component={CountryPage}/>
			<Route path="/" component={HomePage}/>
		</Switch> 
  </Router>,
  document.getElementById('root')
);
