import React from 'react';

import './CovidTable.css';
import arrow_up from './arrow_up.png';
import arrow_up_active from './arrow_up_active.png';

export default class CovidSummary extends React.Component {
    constructor() {
        super();
        this.state = {
            data: null,
            sortMethod: null,
        }
    }

    componentDidMount() {
        let execute = (resp) => {
            let data = this.props.extract(resp);
            this.setState({
                data, sortMethod: [0, 1] //column 0, increasing
            }); //will update from template
        };

        let retry = () => {
            this.props.load().then(
                resp => resp.json()
            ).then(
                execute
            ).catch(err => retry);
        }; retry();
    }

    shouldComponentUpdate(nextprops, nextstate) {
        if ( this.state.sortMethod && nextstate.sortMethod[0] == this.state.sortMethod[0] && nextstate.sortMethod[1] == this.state.sortMethod[1] )
            return false;

        return true;
    }

    render() {
        return (
            <div>
                <h1 className="grey header">{this.props.title}</h1>
                {this.state.data ? 
                    <table className="summary table">
                        <thead>
                            <tr>
                                <td><span>Country</span></td>
                                <td><span>New Cases</span></td>
                                <td><span>Total Cases</span></td>
                                <td><span>New Recoveries</span></td>
                                <td><span>Total Recoveries</span></td>
                                <td><span>New Deaths</span></td>
                                <td><span>Total Deaths</span></td>
                            </tr>
                            <tr>
                                <td><i className="arrow_up active"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                                <td><i className="arrow_up"/><i className="arrow_down"/></td>
                            </tr>
                        </thead>
                        <tbody>
                            {(console.log(this.state) || this.state.data).map((data, idx) => (
                                <tr key={idx}>
                                    <td><span>{data.Country}</span>({data.CountryCode})</td>
                                    <td>{data.NewConfirmed}</td>
                                    <td>{data.TotalConfirmed}</td>
                                    <td>{data.NewRecovered}</td>
                                    <td>{data.TotalRecovered}</td>
                                    <td>{data.NewDeaths}</td>
                                    <td>{data.TotalDeaths}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    :
                    <table className="summary">
                        <tbody>
                            <tr><td>
                                <div className="loading">
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div>
                            </td></tr>
                        </tbody>
                    </table>
                }
            </div>
        )
    }
};
